mapper.select:
SELECT name, status, daily_report_time AS dailyReportTime
FROM Project
WHERE project_id = #{projectId}

SELECT COUNT(*)
FROM ProjectMember
WHERE project_id = #{projectId}
  AND user_id = #{userId}
  AND is_favorite = 1

mapper.update:
UPDATE ProjectMember
SET is_favorite = 1    WHERE project_id = #{projectId}
                         AND user_id = #{userId}

UPDATE ProjectMember
SET is_favorite = 0    WHERE project_id = #{projectId}
                         AND user_id = #{userId}
